import React from "react";
// We import bootstrap to make our application look better.
import "bootstrap/dist/css/bootstrap.css";

// We import NavLink to utilize the react router.
import { NavLink } from "react-router-dom";


// Here, we display our Navbar
export default function Navbar() {
  return (
    <div>

      <div className="m-auto"><h4>MERN Stack</h4></div>
      <nav className="navbar navbar-light bg-success text-white">

        <NavLink className="nav-link enable-button-pointers mx-auto" to="/create">
          <h4 className="">Create Record</h4>
        </NavLink>
        <NavLink className="nav-link enable-button-pointers pe-3" to="/">
          <i class="fi fi-br-cross hover:bg-light p-2"></i>
        </NavLink>


      </nav>
    </div>
  );
}
